import { EditorToolbar } from "@wpmudev/sui-editor-toolbar"
import { Button } from "@wpmudev/sui-button"
import {
	SegmentedControl,
	SegmentedControlButton,
} from "@wpmudev/sui-segmented-control"

    const actions = (settingBtn) => {
      return [
         <Button
           key={0}
           appearance="tertiary"
           icon="Save"
           color="black"
           isSmall={true}
           isResponsive={true}
         >
           Save Draft
         </Button>,
         <Button
           key={1}
           appearance="secondary"
           icon="Show"
           color="black"
           isSmall={true}
           isFullWidth={true}
           isResponsive={true}
         >
           Preview
         </Button>,
    	     <Button
            key={2}
            appearance="primary"
            icon="Globe"
            color="blue"
            isSmall={true}
            isFullWidth={true}
         >
            Publish
         </Button>,
          settingBtn,
        ]
    }

    export const Example = () => {
      return (
        <EditorToolbar title="Toolbar Title" actions={actions}>
          <SegmentedControl name="type">
            <SegmentedControlButton
               icon="Desktop"
               isResponsive={true}
               value="visual"
             >
                Visual
             </SegmentedControlButton>
            <SegmentedControlButton icon="Desktop" isResponsive={true} value="code">
                Code
            </SegmentedControlButton>
          </SegmentedControl>
        </EditorToolbar>
      )
    }

